let history = [];

function Point(x, y, type) {
  this.x = x;
  this.y = y;
  this.type = type; // 左击 1  右击 3
}

function windowToCanvas(e, mycanvas) {
  // 返回元素的大小以及位置
  let rect = mycanvas.getBoundingClientRect(); // rect 的宽度会加上 canvas 的 border 会影响精度

  return new Point(e.offsetX - rect.left * (mycanvas.width / rect.width), e.offsetY - rect.top * (mycanvas.height / rect.height), e.which);
}

function showLastHistory(ctx, history) {
  ctx.putImageData(history[history.length - 1].data, 0, 0);
}

function addHistoy(history, ctx, mycanvas) {
  console.log("存历史");
  console.log(history);
  history.push({
    data: ctx.getImageData(0, 0, mycanvas.width, mycanvas.height)
  });
}

