      ctx.fillStyle = '#f00000';
      ctx.strokeStyle = '#f00000'; // 画笔颜色

      ctx.beginPath();
      ctx.arc(x, y, 3, 0, Math.PI * 2, true);
      ctx.closePath();
      ctx.fill();
      ctx.restore();
      this.posArray.push({
        data: [x, y]
      });
