    //  多边形个顶点连线，画多边形
    drawPolygon() {
      //排序，决定个点之间的连线顺序，尽量避免各边相交
      let ps = this.sortPoints(this.point);
    },

    sortPoints(points) {
      //让点按x轴升序排序
      let tempPoints = points.sort((p1, p2) => {
        return p1.x - p2.x;
      }); //找到x轴的左右极点

      let firstP = tempPoints[0]; //x轴最左端

      let lastP = tempPoints[tempPoints.length - 1]; //x轴最右端
      //如果多个x轴的左右极点，则按y轴排出左右极点

      let smallXP = tempPoints.filter(ball => ball.x === firstP.x);
      let bigXP = tempPoints.filter(ball => ball.x === lastP.x); // 处理左右极点有多个的情况,y轴降序

      if (smallXP.length > 1) {
        smallXP.sort((ballA, ballB) => {
          return ballB.y - ballA.y;
        });
      }

      if (bigXP.length > 1) {
        bigXP.sort((ballA, ballB) => {
          return ballB.y - ballA.y;
        });
      }

      firstP = smallXP[0];
      lastP = bigXP[0]; // 获得两个极点连线的角度

      let splitLineAngle = Math.atan2(lastP.y - firstP.y, lastP.x - firstP.x);
      let upperP = []; //上点  小于splitLineAngle的都是上点  X轴降序连接

      let lowerP = []; //下点  大于splitLineAngle的都是下点  X轴升序连接
      //计算弧度  分为上下点数组

      tempPoints.forEach(p => {
        if (p === firstP || p === lastP) {
          return false;
        }

        let angle = Math.atan2(p.y - firstP.y, p.x - firstP.x);

        if (angle > splitLineAngle) {
          lowerP.push(p);
        } else {
          upperP.push(p);
        }
      }); //升序

      lowerP = lowerP.sort((p1, p2) => {
        if (p1.x !== p2.x) {
          // 处理X轴相同情况的排序
          return p1.x - p2.x;
        }

        return p2.y - p1.y;
      }); //降序

      upperP = upperP.sort((p1, p2) => {
        if (p1.x !== p2.x) {
          // 处理X轴相同情况的排序
          return p2.x - p1.x;
        }

        return p2.y - p1.x;
      }); // 逆时针连接所有的点

      let myPoints = [firstP].concat(lowerP, [lastP], upperP); //调用画直线的方法，进行连线

      let length = myPoints.length;

      for (let i = 0; i < length - 1; i++) {
        this.drawerLine(this.ctx, myPoints[i].x, myPoints[i].y, myPoints[i + 1].x, myPoints[i + 1].y);
        console.log(i);
      }

      this.drawerLine(this.ctx, myPoints[length - 1].x, myPoints[length - 1].y, myPoints[0].x, myPoints[0].y);
      this.getPolygonArea(); //计算多边形体积
      //画完清空

      this.point = [];
      myPoints = [];
    },

